import React, { useState } from 'react';
import axios from 'axios';

const InputForm = () => {
    const [formData, setFormData] = useState({
        text: '',
        checkbox: false,
        radio: '',
        dropdown: '',
        color: '#000000',
        date: '',
        datetime: '',
        url: '',
        range: '',
        file: null,
        audio: null,
        video: null,
        image: null
    });

    const handleChange = (e) => {
        const { name, value, type, checked, files } = e.target;
        if (type === 'checkbox') {
            setFormData({ ...formData, [name]: checked });
        } else if (type === 'color') {
            setFormData({ ...formData, [name]: value });
        } else if (type === 'date') {
            setFormData({ ...formData, [name]: value });
        } else if (type === 'datetime-local') {
            setFormData({ ...formData, [name]: value });
        } else if (type === 'range') {
            setFormData({ ...formData, [name]: value });
        } else if (type === 'file') {
            setFormData({ ...formData, [name]: files[0] });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formDataToSend = new FormData();
        Object.keys(formData).forEach(key => {
            formDataToSend.append(key, formData[key]);
        });

        try {
            const response = await axios.post('http://localhost:5000/submit', formDataToSend, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            alert(response.data.message);
        } catch (error) {
            console.error('Error submitting form data', error);
            alert('Error submitting form data');
        }
    };

    return (
        <>
            {/* Existing form elements */}
            <label className="block mb-4">
                <span className="text-gray-700">Color:</span>
                <input type="color" name="color" onChange={handleChange} required className="form-input mt-1 block" />
            </label>
            <label className="block mb-4">
                <span className="text-gray-700">Date:</span>
                <input type="date" name="date" onChange={handleChange} required className="form-input mt-1 block" />
            </label>
            <label className="block mb-4">
                <span className="text-gray-700">Datetime:</span>
                <input type="datetime-local" name="datetime" onChange={handleChange} required className="form-input mt-1 block" />
            </label>
            <label className="block mb-4">
                <span className="text-gray-700">URL:</span>
                <input type="url" name="url" onChange={handleChange} required className="form-input mt-1 block" />
            </label>
            <label className="block mb-4">
                <span className="text-gray-700">Range:</span>
                <input type="range" name="range" onChange={handleChange} min="1" max="100" required className="form-input mt-1 block" />
            </label>
            <label className="block mb-4">
                <span className="text-gray-700">Image:</span>
                <input type="file" name="image" accept="image/*" onChange={handleChange} required className="form-input mt-1 block" />
            </label>
            {/* Rest of the form */}
        </>
    );
};

export default InputForm;
