import React, { useState } from 'react';
import axios from 'axios';

const InputForm = () => {
  const [formData, setFormData] = useState({
    text: '',
    checkbox: false,
    radio: '',
    dropdown: '',
    file: null,
    audio: null,
    video: null
  });

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === 'checkbox') {
      setFormData({ ...formData, [name]: checked });
    } else if (type === 'file') {
      setFormData({ ...formData, [name]: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formDataToSend = new FormData();
    Object.keys(formData).forEach(key => {
      formDataToSend.append(key, formData[key]);
    });

    try {
      const response = await axios.post('http://localhost:5000/submit', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      alert(response.data.message);
    } catch (error) {
      console.error('Error submitting form data', error);
      alert('Error submitting form data');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Text:
        <input type="text" name="text" onChange={handleChange} required />
      </label>
      <br />
      <label>
        Checkbox:
        <input type="checkbox" name="checkbox" onChange={handleChange} />
      </label>
      <br />
      <label>
        Radio Button:
        <input type="radio" name="radio" value="option1" onChange={handleChange} /> Option 1
        <input type="radio" name="radio" value="option2" onChange={handleChange} /> Option 2
      </label>
      <br />
      <label>
        Dropdown:
        <select name="dropdown" onChange={handleChange}>
          <option value="">Select an option</option>
          <option value="option1">Option 1</option>
          <option value="option2">Option 2</option>
        </select>
      </label>
      <br />
      <label>
        File:
        <input type="file" name="file" onChange={handleChange} />
      </label>
      <br />
      <label>
        Audio:
        <input type="file" name="audio" accept="audio/*" onChange={handleChange} />
      </label>
      <br />
      <label>
        Video:
        <input type="file" name="video" accept="video/*" onChange={handleChange} />
      </label>
      <br />
      <button type="submit">Submit</button>
    </form>
  );
};

export default InputForm;
